[code]public void dropItems() {
		Client highestHitter = null;
		int highestHit = -1;
		for (Map.Entry<Client, Integer> entry : previousAttackers.entrySet()) {
			if (entry.getValue() > highestHit) {
				highestHit = entry.getValue();
				highestHitter = entry.getKey();
			}
		}
		for(int i = 0; i < client.playerItems.length; i++) {
			if(client.playerItems[i] <= 0) continue;
			FloorItem item = new FloorItem((client.playerItems[i]-1), client.playerItemsN[i], highestHitter, client.getAbsX(), client.getAbsY(), client.getHeightLevel(), client.currentRegion);
			if(destroyItem(item.getId())) { 
			    item = new FloorItem(item.getId(), 1, client, client.getAbsX(), client.getAbsY(), client.getHeightLevel(), client.currentRegion);
			    Server.getItemManager().newPrivateDrop(item, client);
				continue;
			}
			if(item.getId() == -1 || item == null) {
				continue; 
			}
				String name = Server.getItemManager().getItemDefinition(item.getId()).getName();
				if((name.startsWith("Ahrims") || name.startsWith("Dharoks") || name.startsWith("Guthans") || name.startsWith("Karils") || name.startsWith("Torags") || name.startsWith("Veracs")) && !name.endsWith("0")){
					item = new FloorItem(Item.switchBarrowsItems(item.getId()), 1, highestHitter, client.getAbsX(), client.getAbsY(), client.getHeightLevel(), client.currentRegion);
				}
			if(highestHitter != null) {
				Server.getItemManager().newDrop(item, highestHitter);
			} else {
				Server.getItemManager().newDrop(item, null);
				Server.getItemManager().showDrop(item);
			}
		}
