procedure SafeInitForm;
var
  V : TVariantArray;
begin
  SetArrayLength(V, 0);
  ThreadSafeCall('InitForm', V);
end;

procedure ShowInitFormModal;
begin
  frmDesign.ShowModal;
end;

procedure SafeShowInitFormModal;
var
  V : TVariantArray;
begin
  SetArrayLength(V, 0);
  ThreadSafeCall('ShowInitFormModal', V);
end;

procedure MainInitForm;
begin
  SafeInitForm;
  SafeShowInitFormModal;
  FreeForm(frmDesign);
end;

begin
  ClearDebug;
  MainInitForm;
  if(EndInitForm)then
    TerminateScript;
