				if (left==-1||right==-1) {
					System.out.println("Error: Droptable is missing closing \"]\"");
					continue;
				}
				npcId=Integer.parseInt(line.substring(line.indexOf("+")+1,line.indexOf("+",line.indexOf("+")+1)));
				if (npcId<0)
					continue;
				System.out.println("NPC: "+npcId);


