						c.followDistance = 5;
					}
					c.hitDelay = getHitDelay(c.getItems()
							.getItemName(c.playerEquipment[c.playerWeapon])
							.toLowerCase());
					c.oldPlayerIndex = i;
					c.oldSpellId = c.spellId;
					c.spellId = 0;
					Client o = (Client) PlayerHandler.players[i];
